const rooms = msg.payload.rooms;
const results = []
for(let room of rooms) {
    if (room.height < 2.1) {
        results.push({ name: room.name, result: "居室の天井の高さは、二・一メートル以上でなければなりません。" });
        continue
    } 
    results.push({ name: room.name, result: "OK" });
}

let newMsg = { payload: results };
newMsg.req = msg.req;
newMsg.res = msg.res;

return newMsg;
